package com.tns.ifet.day1;

public class PrimitiveDataTypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//byte -1byte
		byte byteMax=127;
		byte byteMin=-128;
		System.out.println("Min range of byte is"+byteMin+"Max range of byte is"+byteMax);
		//short takes 2 bytes
		short shortMax=32767;
		short shortMin=-32768;
		System.out.println("Minshort range  of byte is"+shortMin+"Minshort range of byte is:"+shortMax);
		//int-4 bytes
		int maxInt=2147483647;
		int minInt=-2147483648;
		System.out.println("Maxint range of byte is:"+maxInt+"Minint range of byte is:"+minInt);
		//long-8bytes
		long maxLong=9223372036854775807L;
		long minLong=-9223372036854775808L;
		System.out.println("MinLong range of byte is:"+maxLong+"MaxLong range of byte is:"+minLong);
		//float
		float f=3224.141243278345f;
		double d=345.14124512345678902345678914f;
		System.out.println("float value is:"+f+"double value is:"+d);
		//boolean
		boolean flag=false;
		System.out.println("boolean value is:"+flag);
		

	}

}
